from aiogram import Bot, Dispatcher, types
from aiogram.types import InlineKeyboardMarkup, InlineKeyboardButton, ReplyKeyboardMarkup, KeyboardButton
from aiogram.types.web_app_info import WebAppInfo
from aiogram.filters import Command
from bot_token import token
import asyncio

bot = Bot(token=token)
dp = Dispatcher()

@dp.message(Command("start"))
async def info(message: types.Message):
    name = message.from_user.first_name
    surname = message.from_user.last_name
    initials = f"{name} {surname}" if surname else name
    keyboard = ReplyKeyboardMarkup(
        keyboard=[
            [KeyboardButton(text="–û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è"),KeyboardButton(text="–ó–∞–±—Ä–æ–Ω–∏—Ä–æ–≤–∞—Ç—å –Ω–æ–º–µ—Ä")],
            [KeyboardButton(text="–û–±—Ä–∞—Ç–∏—Ç—å—Å—è –∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—É –æ—Ç–µ–ª—è")]
        ],
        resize_keyboard=True
    )
    await message.answer(f"–î–æ–±—Ä–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏ —Å—É—Ç–æ–∫, {initials}!", reply_markup=keyboard)

@dp.message(Command("info"))
async def InfoHandler(message: types.Message):
    await message.answer("<b>–ß—Ç–æ –º–æ–∂–Ω–æ —Å–∫–∞–∑–∞—Ç—å –æ –Ω–∞—à–µ–º –æ—Ç–µ–ª–µ? –î–∞–≤–∞–π —è —Ç–µ–±–µ –ø–æ–≤–µ–¥–∞—é ü¶æ</b>\n", parse_mode='HTML')
    await message.answer('–û—Ç–µ–ª—å "–î—é–Ω—ã –ó–æ–ª–æ—Ç—ã–µ" –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ 4* (–∑–≤–µ–∑–¥—ã) —Ä–∞–±–æ—Ç–∞–µ—Ç –ø–æ —Å–∏—Å—Ç–µ–º–µ ¬´ –í—Å–µ –≤–∫–ª—é—á–µ–Ω–æ¬ª, –≤–∫–ª—é—á–∞–µ—Ç –≤ —Å–µ–±—è 8 –∫–æ—Ç—Ç–µ–¥–∂–µ–π –∏ 3 –∫–æ—Ä–ø—É—Å–∞ —Å –Ω–æ–º–µ—Ä–Ω—ã–º —Ñ–æ–Ω–¥–æ–º 116 –Ω–æ–º–µ—Ä–æ–≤ –æ—Ç "–°—Ç–∞–Ω–¥–∞—Ä—Ç–∞" –¥–æ "–°–µ–º–µ–π–Ω—ã—Ö –∞–ø–∞—Ä—Ç–∞–º–µ–Ω—Ç–æ–≤".')
    await message.answer( '¬´–î—é–Ω—ã –ó–æ–ª–æ—Ç—ã–µ¬ª ‚Äî —ç—Ç–æ –æ—Ç–ª–∏—á–Ω–æ–µ –º–µ—Å—Ç–æ, —á—Ç–æ–±—ã –æ—Ç–¥–æ—Ö–Ω—É—Ç—å –≤—Å–µ–π —Å–µ–º—å–µ–π –∏–ª–∏ –≤ –∫—Ä—É–≥—É –¥—Ä—É–∑–µ–π, –ø—Ä–æ–≤–µ—Å—Ç–∏ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—ã–π –ø—Ä–∞–∑–¥–Ω–∏–∫, –∞ —Ç–∞–∫–∂–µ –æ—Å—Ç–∞–Ω–æ–≤–∏—Ç—å—Å—è –∑–¥–µ—Å—å, –µ—Å–ª–∏ –≤—ã –ø—Ä–∏–ª–µ—Ç–µ–ª–∏ –≤ –∫–æ–º–∞–Ω–¥–∏—Ä–æ–≤–∫—É. –¢–æ–ª—å–∫–æ –≤ –ª–µ—Ç–Ω–∏–π —Å–µ–∑–æ–Ω –æ—Ç–µ–ª—å –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –±–æ–ª–µ–µ 4500 –≥–æ—Å—Ç–µ–π.')
    await message.answer('- –û–±—â–∞—è –ø–ª–æ—â–∞–¥—å: 2,2 –≥–∞ –∑–∞–∫—Ä—ã—Ç–æ–π —Ç–µ—Ä—Ä–∏—Ç–æ—Ä–∏–∏\n- –†–µ—Å—Ç–æ—Ä–∞–Ω "—à–≤–µ–¥—Å–∫–æ–π –ª–∏–Ω–∏–∏" ¬´Le Dune¬ª\n- –ë–∞—Ä ¬´Acapulco¬ª —Å —É—é—Ç–Ω–æ–π —Ç–µ—Ä—Ä–∞—Å–æ–π\n- –°–æ–±—Å—Ç–≤–µ–Ω–Ω—ã–π –ø–µ—Å—á–∞–Ω—ã–π –ø–ª—è–∂: 3‚Äâ500 –º¬≤\n- –ü–ª—è–∂–Ω–∞—è –∑–æ–Ω–∞ —Å–æ –≤—Å–µ–º–∏ —É–¥–æ–±—Å—Ç–≤–∞–º–∏\n- –ü–ª—è–∂–Ω—ã–π –±–∞—Ä ¬´Tamarix¬ª\n- –î–µ—Ç—Å–∫–∏–π –ø–∞—Ä–∫ —Ä–∞–∑–≤–ª–µ—á–µ–Ω–∏–π ¬´D—é–Ω–æL—ç–Ω–¥¬ª –∏ –º–∏–Ω–∏-–∫–ª—É–±\n- SPA –∫–æ–º–ø–ª–µ–∫—Å\n- –ü–æ–¥–æ–≥—Ä–µ–≤–∞–µ–º—ã–π –±–∞—Å—Å–µ–π–Ω "–õ–∞–≥—É–Ω–∞" ‚Äî 108 –º¬≤\n- –ú–Ω–æ–∂–µ—Å—Ç–≤–æ —É–≥–æ–ª–∫–æ–≤ –¥–ª—è —É–µ–¥–∏–Ω–µ–Ω–∏—è\n- –ß—É–¥–µ—Å–Ω—ã–π –ª–∞–Ω–¥—à–∞—Ñ—Ç –∏ –≤–∏–¥ –Ω–∞ –±–µ—Å–∫—Ä–∞–π–Ω–µ–µ –º–æ—Ä–µ')
    await message.answer('–õ–µ—Ç–æ ‚Äî –ø–æ—Ä–∞ –æ—Ç–ø—É—Å–∫–æ–≤, –ø–ª—è–∂–Ω–æ–≥–æ –æ—Ç–¥—ã—Ö–∞ –∏ –º–æ—Ä—Å–∫–∏—Ö —Ä–∞–∑–≤–ª–µ—á–µ–Ω–∏–π. –ò—é–Ω—å –º–æ–∂–Ω–æ –æ—Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏–∑–æ–≤–∞—Ç—å –ª—é–±–∏–º—ã–º –º–µ—Å—è—Ü–µ–º –¥–ª—è —Ç–µ—Ö, –∫—Ç–æ –Ω–µ –ø—Ä–∏–≤—ã–∫ –∫ —Å–∏–ª—å–Ω–æ–º—É –∑–Ω–æ—é: –¥–Ω–µ–º —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –≤–æ–∑–¥—É—Ö–∞ –ø–æ–¥–Ω–∏–º–∞–µ—Ç—Å—è –¥–æ +25‚Ä¶+28, –∞ –Ω–æ—á—å—é –Ω–µ –æ–ø—É—Å–∫–∞–µ—Ç—Å—è –Ω–∏–∂–µ +18‚Ä¶+20, –≤–æ–¥–∞ —Ç–∞–∫–∂–µ –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ, –Ω–æ —É–≤–µ—Ä–µ–Ω–Ω–æ –ø—Ä–æ–≥—Ä–µ–≤–∞–µ—Ç—Å—è –∏ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è –ø–æ–¥—Ö–æ–¥—è—â–µ–π –¥–ª—è –∫—É–ø–∞–Ω–∏—è –±–ª–∏–∂–µ –∫ —Å–µ—Ä–µ–¥–∏–Ω–µ –º–µ—Å—è—Ü–∞. –ò—é–ª—å –∏ –∞–≤–≥—É—Å—Ç ‚Äî –Ω–∞–∏–±–æ–ª–µ–µ –∑–Ω–æ–π–Ω—ã–µ –º–µ—Å—è—Ü—ã –≤ –ê–Ω–∞–ø–µ, –Ω–µ—Ä–µ–¥–∫–æ –≤ —ç—Ç–æ –≤—Ä–µ–º—è —Å—Ç–æ–ª–±–∏–∫–∏ —Ç–µ—Ä–º–æ–º–µ—Ç—Ä–æ–≤ –ø–æ–∫–∞–∑—ã–≤–∞—é—Ç +35‚Ä¶+40 –≤ –¥–Ω–µ–≤–Ω–æ–µ –≤—Ä–µ–º—è. –ó–∞—Ç–æ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –≤–æ–¥—ã –≤ —ç—Ç–æ –≤—Ä–µ–º—è –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ –∏–¥–µ–∞–ª—å–Ω–∞—è –¥–ª—è –≤—Å–µ—Ö ‚Äî –∏ –¥–ª—è –≤–∑—Ä–æ—Å–ª—ã—Ö, –∏ –¥–ª—è –¥–µ—Ç–∏—à–µ–∫, –æ–Ω–∞ –∫–æ–ª–µ–±–ª–µ—Ç—Å—è –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö +24‚Ä¶+27. –û—Ç—Å—É—Ç—Å—Ç–≤–∏–µ –¥–æ–∂–¥–µ–π, —Ç–µ–ø–ª—ã–π, –¥–∞–∂–µ –∂–∞—Ä–∫–∏–π –≤–æ–∑–¥—É—Ö –∏ –Ω–µ–º–Ω–æ–≥–æ –æ—Ö–ª–∞–∂–¥–∞—é—â–∏–π –µ–≥–æ –º–æ—Ä—Å–∫–æ–π –±—Ä–∏–∑ ‚Äî –∏–º–µ–Ω–Ω–æ —Ç–∞–∫ –º–æ–∂–Ω–æ –æ—Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏–∑–æ–≤–∞—Ç—å –∫–ª–∏–º–∞—Ç –ª–µ—Ç–Ω–µ–π –ê–Ω–∞–ø—ã.')

@dp.message(lambda message: message.text.lower() == "–æ—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è")
async def InfoHandler(message: types.Message):
    await message.answer('üñ•Ô∏è –°–∞–π—Ç "–ó–æ–ª–æ—Ç—ã–µ –î—é–Ω—ã" - https://clck.ru/39QvtG\n\nüìé –¢–µ–ª–µ–≥—Ä–∞–º –∫–∞–Ω–∞–ª - https://clck.ru/39QvuP\n\nüì± –°—Ç—Ä–∞–Ω–∏—Ü–∞ –í–∫–æ–Ω—Ç–∞–∫—Ç–µ - https://clck.ru/39TGpE\n\nüèñÔ∏è –ö—É—Ä–æ—Ä—Ç–Ω—ã–π —Å–±–æ—Ä - https://clck.ru/39S4TB\n\nüìç –ê–¥—Ä–µ—Å - –≥.–ê–Ω–∞–ø–∞, –î–∂–µ–º–µ—Ç–µ, –ü–∏–æ–Ω–µ—Ä—Å–∫–∏–π –ø—Ä–æ—Å–ø–µ–∫—Ç 223\n\nüß≠ –ì—É–≥–ª –∫–∞—Ä—Ç—ã - https://clck.ru/39RkrK\n\nüß≠ –Ø–Ω–¥–µ–∫—Å –∫–∞—Ä—Ç—ã - https://clck.ru/39RmPs')

@dp.message(lambda message: message.text.lower() == "–∑–∞–±—Ä–æ–Ω–∏—Ä–æ–≤–∞—Ç—å –Ω–æ–º–µ—Ä")
async def TakeHotel(message: types.Message):
    await message.answer('‚ùì –ß—Ç–æ–±—ã –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è —Å —Ü–µ–Ω–æ–≤–æ–π –ø–æ–ª–∏—Ç–∏–∫–æ–π –Ω–∞—à–µ–≥–æ –æ—Ç–µ–ª—è, –∏—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–æ–º–∞–Ω–¥—É /price\n(–Ω–∞–ø–∏—à–∏—Ç–µ –∏–ª–∏ –ø—Ä–æ—Å—Ç–æ –Ω–∞–∂–º–∏—Ç–µ –Ω–∞ –Ω–µ—ë)\n\n‚ùóÔ∏è –¢–∞–∫–∂–µ –∫–∞–∫ –∏ –≤ –¥—Ä—É–≥–∏—Ö –æ—Ç–µ–ª—è—Ö, —É –Ω–∞—Å –ø—Ä–∏—Å—É—Ç—Å—Ç–≤—É–µ—Ç [–∫—É—Ä–æ—Ä—Ç–Ω—ã–π —Å–±–æ—Ä](https://clck.ru/39S4TB), —Å –Ω–∏–º –º–æ–∂–Ω–æ –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è, –Ω–∞–∂–∞–≤ –≥–ª–∞–≤–Ω—É—é –∫–Ω–æ–ø–∫—É —Å–Ω–∏–∑—É "–û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è", –∏–ª–∏ –Ω–∞–∂–∞—Ç—å –Ω–∞ —Å–∞–º–æ —Å–ª–æ–≤–æ\n\n‚úîÔ∏è –î–ª—è –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –Ω–æ–º–µ—Ä–∞, –Ω–∞–∂–º–∏—Ç–µ –≥–ª–∞–≤–Ω—É—é –∫–Ω–æ–ø–∫—É —Å–Ω–∏–∑—É "–û–±—Ä–∞—Ç–∏—Ç—å—Å—è –∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—É –æ—Ç–µ–ª—è"', parse_mode='Markdown')

@dp.message(lambda message: message.text.lower() == "–æ–±—Ä–∞—Ç–∏—Ç—å—Å—è –∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—É –æ—Ç–µ–ª—è")
async def TalkWithADM(message: types.Message):
    await message.answer('üíª –¢–µ–ª–µ–≥—Ä–∞–º –¥–∏–∞–ª–æ–≥ - https://clck.ru/39Rn4T\n\n‚òé –ù–æ–º–µ—Ä - +7-958-497-0515\n\n‚òé –ù–æ–º–µ—Ä - +8-800-505-8552\n\nüìß –≠–ª–µ–∫—Ç—Ä–æ–Ω–Ω–∞—è –ø–æ—á—Ç–∞ - sales@dunesgold.ru')

@dp.message(Command("price"))
async def HotelPrice(message: types.Message):
    keyboard = InlineKeyboardMarkup(
        inline_keyboard=[
            [InlineKeyboardButton(text="–ù–∞–∂–º–∏—Ç–µ –Ω–∞ –º–µ–Ω—è, –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –Ω–æ–≤–æ–≥–æ –æ–∫–Ω–∞",web_app=WebAppInfo(url="https://dunesgold.ru/tabron#popup:inf"))]
        ]
    )
    await message.answer("–û–∫–æ–Ω—á–∞—Ç–µ–ª—å–Ω–∞—è —Ü–µ–Ω–∞ –Ω–æ–º–µ—Ä–∞ –∑–∞–≤–∏—Å–∏—Ç –æ—Ç –º–Ω–æ–≥–∏—Ö —Ñ–∞–∫—Ç–æ—Ä–æ–≤ ‚ùóÔ∏è\n–ù–µ —Å—Ç–æ–∏—Ç –∏ –∑–∞–±—ã–≤–∞—Ç—å, —á—Ç–æ —É –Ω–∞—Å –ø—Ä–∏—Å—É—Ç—Å—Ç–≤—É–µ—Ç [–∫—É—Ä–æ—Ä—Ç–Ω—ã–π —Å–±–æ—Ä](https://clck.ru/39S4TB)", reply_markup=keyboard, parse_mode='Markdown')

async def main():
    await dp.start_polling(bot)

if __name__ == '__main__':
    try:
        print(".: Launched")
        asyncio.run(main())
    except KeyboardInterrupt:
        print(".: Stopped")